// Viết hàm findMax tìm số lớn nhất trong mảng
function findMax(arr) {
  // Code của bạn ở đây

}

// Test cases
console.log(findMax([1, 5, 3, 9, 2]));  // 9
console.log(findMax([-1, -5, -2]));     // -1
console.log(findMax([42]));             // 42

exercises.solution:
function findMax(arr) {
  let max = arr[0];
  for (let i = 1; i < arr.length; i++) {
    if (arr[i] > max) {
      max = arr[i];
    }
  }
  return max;
}

// Hoặc dùng Math.max
// function findMax(arr) {
//   return Math.max(...arr);
// }

console.log(findMax([1, 5, 3, 9, 2]));  // 9
console.log(findMax([-1, -5, -2]));     // -1
console.log(findMax([42]));             // 42

