// Viết hàm filterEven trả về mảng chỉ chứa các số chẵn
function filterEven(arr) {
  // Code của bạn ở đây

}

// Test cases
console.log(filterEven([1, 2, 3, 4, 5, 6]));  // [2, 4, 6]
console.log(filterEven([1, 3, 5]));           // []
console.log(filterEven([0, 2, 4]));           // [0, 2, 4]

exercises.solution:
function filterEven(arr) {
  return arr.filter(num => num % 2 === 0);
}

console.log(filterEven([1, 2, 3, 4, 5, 6]));  // [2, 4, 6]
console.log(filterEven([1, 3, 5]));           // []
console.log(filterEven([0, 2, 4]));           // [0, 2, 4]

